import axios from 'axios';



export const fetchHotels = async (
  cityId: string,
  checkIn: string,
  checkOut: string,
  adults: number,
  children: number
) => {
  try {
    const response = await axios.get('https://api.makcorps.com/city', {
      params: {
        api_key: '67d0875938d0825a40cc632d', 
        cityid: cityId,
        pagination: 0, 
        cur: 'USD', 
        rooms: 1,
        adults: adults,
        children: children,
        checkin: checkIn,
        checkout: checkOut,
      },
    });
    return response.data; // Returns an array of hotels
  } catch (error) {
    console.error('Error fetching hotels:', error);
    return [];
  }
};


interface Hotel {
  name: string;
  geocode: {
    latitude: number;
    longitude: number;
  };
  telephone: string;
  reviews: {
    rating: number;
    count: number;
  };
  vendor1?: string;
  price1?: string;
}

const HotelResults: React.FC<{ hotels: Hotel[] }> = ({ hotels }) => {
  return (
    <div>
      <h3>Hotel Results:</h3>
      {hotels.map((hotel, index) => (
        <div key={index}>
          <h4>{hotel.name}</h4>
          <p>Rating: {hotel.reviews.rating} ({hotel.reviews.count} reviews)</p>
          <p>Price: {hotel.price1 || 'N/A'}</p>
          <p>Vendor: {hotel.vendor1 || 'N/A'}</p>
        </div>
      ))}
    </div>
  );
};

export default HotelResults;
