import axios from 'axios';
import { useState,useEffect } from 'react';


const fetchCityId = async (destination: string) => {
  try {
    const response = await axios.get('https://api.makcorps.com/mapping', {
      params: {
        api_key: '67d0875938d0825a40cc632d',
        name: destination,
      },
    });
    return response.data;
  } catch (error) {
    console.error('Error fetching city ID:', error);
    return [];
  }
};


interface CityResult {
  document_id: string;
  name: string;
  details: {
    parent_name: string;
    grandparent_name: string;
  };
}

const CitySearch: React.FC = () => {
  const [destination, setDestination] = useState('');
  const [cityResults, setCityResults] = useState<CityResult[]>([]);
  const [selectedCityId, setSelectedCityId] = useState<string | null>(null);

  const handleSearch = async () => {
    const results = await fetchCityId(destination);
    setCityResults(results);
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Enter destination"
        value={destination}
        onChange={(e) => setDestination(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>

      {cityResults.length > 0 && (
        <div>
          <h3>Select a City:</h3>
          <ul>
            {cityResults.map((city) => (
              <li key={city.document_id}>
                <button onClick={() => setSelectedCityId(city.document_id)}>
                  {city.name} - {city.details.parent_name}, {city.details.grandparent_name}
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}

      {selectedCityId && <p>Selected City ID: {selectedCityId}</p>}
    </div>
  );
};

export default CitySearch;
