const fetchHotels = async (
  cityId: string,
  checkIn: string,
  checkOut: string,
  adults: number,
  children: number
) => {
  try {
    const response = await axios.get('https://api.makcorps.com/city', {
      params: {
        api_key: '6571c870c2a0a227d4a33ddc', // Replace with your API key
        cityid: cityId,
        pagination: 0, // Start with page 0
        cur: 'USD', // Currency
        rooms: 1, // Number of rooms
        adults: adults,
        children: children,
        checkin: checkIn,
        checkout: checkOut,
      },
    });
    return response.data; // Returns an array of hotels
  } catch (error) {
    console.error('Error fetching hotels:', error);
    return [];
  }
};
