import React, { useEffect, useState } from "react";
import Post from "../";
import axios from "axios";

// Define the TypeScript interface for your post data
interface PostType {
  _id: string;
  title: string;
  author: string;
  content: string;
  createdAt: string;
}

export default function Home() {
  const [data, setData] = useState<PostType[]>([]); // ✅ Explicitly define state type

  useEffect(() => {
    axios
      .get("http://localhost:3000/posts")
      .then((res) => {
        setData(res.data); // ✅ TypeScript now knows res.data is an array of PostType
      })
      .catch((err) => {
        console.error("Error fetching posts:", err);
      });
  }, []);

  return (
    <>
      {data.length > 0 ? (
        data.map((post) => (
          <Post
            key={post._id} // ✅ Fix: TypeScript now recognizes _id
            title={post.title}
            auther={post.author} // ✅ Fix: Change 'auther' to 'author' to match backend response
            content={post.content}
            createdAt={post.createdAt}
          />
        ))
      ) : (
        <p>Loading posts...</p>
      )}
    </>
  );
}
