import React, { useState, JSX, useEffect } from "react";
import Input from '../components/input'
import axios from "axios";
interface PostData {
  author: string;
  title: string;
  content: string;
}

export default function CreatePostForm(): JSX.Element {

  const [author, setAuthor] = useState('')
  const [title, setTitle] = useState('')
  const [content, setContent] = useState('')
  const handleSubmit = () => {
    axios.post("http://localhost:3000/posts").then((res => {
      console.log(res);
    }))
  }

  return (
    <div className="max-w-lg mx-auto p-6 bg-white shadow-md rounded-lg">
      <h2 className="text-2xl font-bold mb-4 text-center">Create a New Post</h2>

      <form onSubmit={handleSubmit} className="space-y-4">

        <Input name='auther'
          placeholder="Author Name"
          value={author}
          onChange={(e) => setAuthor(e.target.value)} />
        <Input
          name="title"
          placeholder="Post Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <textarea
          name="content"
          placeholder="Post Content"
          value={content}
          onChange={(e) => setContent(e.target.value)}
          rows={4}
          className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-green-200"
        />
        <button type="submit" className="w-full bg-green-500 text-white p-2 rounded hover:bg-green-600 focus:outline-none ">
          Publish Post
        </button>
      </form>
    </div>
  );
}
