// src/App.tsx
import React, { useState } from 'react';
import SearchForm from './components/formSearch/';
import HotelList from './components/HotelResults/HotelList';
import Loading from './components/UI/Loading';
import { Hotel } from './types';

const App: React.FC = () => {
  const [hotels, setHotels] = useState<Hotel[]>([]);
  const [loading, setLoading] = useState(false);
  const [hasSearched, setHasSearched] = useState(false);

  const handleHotelsFound = (foundHotels: Hotel[]) => {
    setHotels(foundHotels);
    setHasSearched(true);
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-blue-600 text-white p-4">
        <div className="container mx-auto">
          <h1 className="text-3xl font-bold">Hotel Finder</h1>
        </div>
      </header>

      <main className="container mx-auto py-8 px-4">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          <div className="lg:col-span-1">
            <SearchForm 
              onHotelsFound={handleHotelsFound} 
              setLoading={setLoading} 
            />
          </div>

          <div className="lg:col-span-2">
            {loading ? (
              <Loading />
            ) : hasSearched ? (
              <>
                <h2 className="text-2xl font-bold mb-4">
                  {hotels.length} Hotels Found
                </h2>
                <HotelList hotels={hotels} />
              </>
            ) : (
              <div className="bg-white p-8 rounded-lg shadow-md text-center">
                <h2 className="text-2xl font-bold mb-4">Find Your Perfect Stay</h2>
                <p className="text-gray-600">
                  Use the search form to find available hotels for your trip.
                </p>
              </div>
            )}
          </div>
        </div>
      </main>
    </div>
  );
};

export default App;
