import Post from '../components/post'
import axios from "axios";
import { log } from "console";

 export default function Home():JSX.Element{
   const[data,setData]=useState([])
 useEffect(()=>{
   axios.get('3000/posts').then((res)=>{
      const dta=res.on()
     setData(res.data)
   }).catch((err=>{
      console.log(err);

   }))
 },[])
    return(
      <>
        {data.length > 0 ? (
          data.map((post) => (
            <Post
              key={post._id} 
              title={post.title}
              author={post.author} 
              content={post.content}
              createdAt={post.createdAt}
            />
          ))
        ) : (
          <p>Loading posts...</p>
        )}
      </>
    );
 }
