import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import { PostType } from "../interfaces/post-interface";
export default function PostDetails() {
   const [post, setPost] = useState<PostType | null>(null)
   const [loading, setLoading] = useState(true)
   const { id } = useParams()
   useEffect(() => {
      axios.get(`localhost://3000/posts/${id}`).then((res) => {
         setPost(res.data)
         setLoading(false)
      }).catch(err => {
         console.log(err);
         setLoading(true)
      })
   }, [id])
   if (loading) {
      return <p className="text-red-500 mx-auto text-center">loading....</p>
   }
   if (!post) {
      return <p> this post is not found...</p>
   }
   return (
      <div>
         <h1>{post.author}</h1>
         <span></span>
         <h5>{post.title}</h5>
         <p>{post.content}</p>

      </div>
   )

}
