import React, { useState, JSX } from "react";
import Input from '../components/input'
interface PostData {
  author: string;
  title: string;
  content: string;
}

export default function CreatePostForm(): JSX.Element {
  const [post, setPost] = useState<PostData>({
    author: "",
    title: "",
    content: "",
  });

  const [error, setError] = useState<string | null>(null);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    setPost({ ...post, [e.target.name]: e.target.value });
  };



  return (
    <div className="max-w-lg mx-auto p-6 bg-white shadow-md rounded-lg">
      <h2 className="text-2xl font-bold mb-4 text-center">Create a New Post</h2>
      {error && <p className="text-red-500 text-sm">{error}</p>}
      <form onSubmit={handleSubmit} className="space-y-4">

        <Input name='auther'
          placeholder="Author Name"
          value={post.author}
          onChange={handleChange} />
        <Input
          name="title"
          placeholder="Post Title"
          value={post.title}
          onChange={handleChange}
        />
        <textarea
          name="content"
          placeholder="Post Content"
          value={post.content}
          onChange={handleChange}
          rows={4}
          className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-green-200"
        />
        <button type="submit" className="w-full bg-green-500 text-white p-2 rounded hover:bg-green-600 focus:outline-none ">
          Publish Post
        </button>
      </form>
    </div>
  );
}
