import axios from 'axios'
import React, { useState } from 'react';



const API_KEY = '67d0875938d0825a40cc632d';
const BASE_URL = 'https://api.makcorps.com';

interface searchCityProps {
  onSelectCity: (id: string) => void
}

interface searchCityResult {
  document_id: string;
  name: string; 
  details: {
    parent_name: string; 
    grandparent_name: string; 
  };
}
const CitySearch: React.FC<searchCityProps> = ({ onSelectCity }) => {
  const [destination, setDestination] = useState('')
  const handelSearch = async () => {
    try {
      const response = await axios.get(`${BASE_URL}/mapping`, {
        params: {
          'API_KEY': `${API_KEY}`,
          name:destination
        }
      });
      return response.data;
    } catch (error) {
      console.log(error);

    }
  }

  return <>
<input type="text" onChange={(e)=>setDestination(e.target.value)} className='focused:outline-none border rounded p-3' />
<button onClick={handelSearch}>
  search
</button>
  </>
}


export default CitySearch;
