import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import {findCity,searchHotels} from '../../services/api'
import { City, Hotel } from '../../types';

interface SearchFormProps {
    onHotelsFound: (hotels: Hotel[]) => void;
    setLoading: (loading: boolean) => void;
  }

  interface result {
    destination:string,
    cities:[]
  }
const SearchForm: React.FC<SearchFormProps> = ({ onHotelsFound, setLoading }) => {
  const [destination, setDestination] = useState('');
  const [cities, setCities] = useState<City[]>([]);
  const [selectedCity, setSelectedCity] = useState<City | null>(null);
  const [checkinDate, setCheckinDate] = useState<Date | null>(new Date());
  const [checkoutDate, setCheckoutDate] = useState<Date | null>(new Date(Date.now() + 86400000)); // Next day
  const [adults, setAdults] = useState(1);
  const [children, setChildren] = useState(0);
  const [roomTypes, setRoomTypes] = useState<string[]>([]);
  const [isSearchingCities, setIsSearchingCities] = useState(false);

  const handleCitySearch = async () => {
    if (!destination) return;

    setIsSearchingCities(true);
    try {
      const citiesData = await findCity(destination);
      setCities(citiesData);
    } catch (error) {
      console.error('Error fetching cities:', error);
    } finally {
      setIsSearchingCities(false);
    }
  };

  const handleCitySelect = (city: City) => {
    setSelectedCity(city);
    setCities([]);
  };

  const handleRoomTypeToggle = (type: string) => {
    if (roomTypes.includes(type)) {
      setRoomTypes(roomTypes.filter(t => t !== type));
    } else {
      setRoomTypes([...roomTypes, type]);
    }
  };

  const handleSearch = async () => {
    if (!selectedCity || !checkinDate || !checkoutDate) return;

    setLoading(true);
    try {
      const formattedCheckin = checkinDate.toISOString().split('T')[0];
      const formattedCheckout = checkoutDate.toISOString().split('T')[0];

      const hotels = await searchHotels(
        selectedCity.id,
        formattedCheckin,
        formattedCheckout,
        adults,
        children
      );

      // Filter by room types if selected
      const filteredHotels = roomTypes.length > 0 
        ? hotels.filter(hotel => {
            // You would need to check if the hotel has the selected room types
            // This depends on the actual API response structure
            return true; // Placeholder
          })
        : hotels;

      onHotelsFound(filteredHotels);
    } catch (error) {
      console.error('Error searching hotels:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md w-full">
      <h2 className="text-2xl font-bold mb-4">Find Your Perfect Hotel</h2>

      {/* Destination Search */}
      <div className="mb-4">
        <label className="block text-gray-700 mb-2">Destination</label>
        <div className="relative">
          <input
            type="text"
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
            onBlur={handleCitySearch}
            className="w-full p-2 border border-gray-300 rounded"
            placeholder="Enter city or destination"
          />
          {isSearchingCities && <div className="mt-2">Searching...</div>}

          {cities.length > 0 && (
            <div className="absolute z-10 bg-white w-full mt-1 border border-gray-300 rounded-md shadow-lg">
              {cities.map(city => (
                <div 
                  key={city.id}
                  className="p-2 hover:bg-gray-100 cursor-pointer"
                  onClick={() => handleCitySelect(city)}
                >
                  {city.name}
                </div>
              ))}
            </div>
          )}
        </div>
        {selectedCity && (
          <div className="mt-2 text-green-600">Selected: {selectedCity.name}</div>
        )}
      </div>

      {/* Date Selection */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
        <div>
          <label className="block text-gray-700 mb-2">Check-in Date</label>
          <DatePicker
            selected={checkinDate}
            onChange={(date) => setCheckinDate(date)}
            className="w-full p-2 border border-gray-300 rounded"
            minDate={new Date()}
          />
        </div>
        <div>
          <label className="block text-gray-700 mb-2">Check-out Date</label>
          <DatePicker
            selected={checkoutDate}
            onChange={(date) => setCheckoutDate(date)}
            className="w-full p-2 border border-gray-300 rounded"
            minDate={checkinDate ? new Date(checkinDate.getTime() + 86400000) : new Date()}
          />
        </div>
      </div>

      {/* Guests Selection */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
        <div>
          <label className="block text-gray-700 mb-2">Adults</label>
          <select
            value={adults}
            onChange={(e) => setAdults(Number(e.target.value))}
            className="w-full p-2 border border-gray-300 rounded"
          >
            {[1, 2, 3, 4, 5].map(num => (
              <option key={num} value={num}>{num}</option>
            ))}
          </select>
        </div>
        <div>
          <label className="block text-gray-700 mb-2">Children</label>
          <select
            value={children}
            onChange={(e) => setChildren(Number(e.target.value))}
            className="w-full p-2 border border-gray-300 rounded"
          >
            {[0, 1, 2, 3, 4].map(num => (
              <option key={num} value={num}>{num}</option>
            ))}
          </select>
        </div>
      </div>

      {/* Room Types */}
      <div className="mb-4">
        <label className="block text-gray-700 mb-2">Room Types</label>
        <div className="flex flex-wrap gap-2">
          {['Single', 'Double', 'Suite', 'Family'].map(type => (
            <label key={type} className="inline-flex items-center">
              <input
                type="checkbox"
                checked={roomTypes.includes(type)}
                onChange={() => handleRoomTypeToggle(type)}
                className="form-checkbox h-5 w-5 text-blue-600"
              />
              <span className="ml-2">{type}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Search Button */}
      <button
        onClick={handleSearch}
        disabled={!selectedCity || !checkinDate || !checkoutDate}
        className="w-full bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded focus:outline-none focus:shadow-outline disabled:bg-gray-400"
      >
        Search Hotels
      </button>
    </div>
  );
};

export default SearchForm;
