import React, { useState ,JSX} from "react";
import Input from '../components/input';
import axios from "axios";

export default function CreatePostForm(): JSX.Element {
  const [author, setAuthor] = useState('');
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState<string | null>(null);
  const [errors, setErrors] = useState<{ author?: string; title?: string; content?: string }>({});

  const validateForm = () => {
    let newErrors: { author?: string; title?: string; content?: string } = {};

    if (!author.trim()) newErrors.author = "Author name is required.";
    if (!title.trim()) newErrors.title = "Title is required.";
    if (!content.trim()) newErrors.content = "Content cannot be empty.";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0; 
  };

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    setMessage(null);

    if (!validateForm()) return;

    setLoading(true);

    try {
      const response = await axios.post("http://localhost:3000/posts", { author, title, content });
      setMessage("✅ Post created successfully!");
      setAuthor('');
      setTitle('');
      setContent('');
      setErrors({});
    } catch (error) {
      setMessage("❌ Failed to create post.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-lg mx-auto p-6 bg-white shadow-md rounded-lg">
      <h2 className="text-2xl font-bold mb-4 text-center">Create a New Post</h2>

      {message && (
        <p className={`text-center ${message.includes("✅") ? "text-green-600" : "text-red-600"}`}>
          {message}
        </p>
      )}

      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Author Input */}
        <Input
          name="author"
          placeholder="Author Name"
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
        />
        {errors.author && <p className="text-red-500 text-sm">{errors.author}</p>}

        {/* Title Input */}
        <Input
          name="title"
          placeholder="Post Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        {errors.title && <p className="text-red-500 text-sm">{errors.title}</p>}

        {/* Content Input */}
        <textarea
          name="content"
          placeholder="Post Content"
          value={content}
          onChange={(e) => setContent(e.target.value)}
          rows={4}
          className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-green-200"
        />
        {errors.content && <p className="text-red-500 text-sm">{errors.content}</p>}

        {/* Submit Button */}
        <button
          type="submit"
          className={`w-full bg-green-500 text-white p-2 rounded flex justify-center ${
            loading || Object.keys(errors).length > 0 ? "opacity-50 cursor-not-allowed" : "hover:bg-green-600"
          }`}
          disabled={loading || Object.keys(errors).length > 0}
        >
          {loading ? (
            <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
          ) : (
            "Publish Post"
          )}
        </button>
      </form>
    </div>
  );
}
