import React from 'react';
import { Hotel } from '../../types';

interface HotelCardProps {
  hotel: Hotel;
}

const HotelCard: React.FC<HotelCardProps> = ({ hotel }) => {
  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden">
      {/* Hotel Image - Using a placeholder */}
      <div className="h-48 bg-gray-300 flex items-center justify-center">
        <span className="text-gray-500">Hotel Image</span>
      </div>

      <div className="p-4">
        <h3 className="text-xl font-bold mb-2">{hotel.name}</h3>

        {/* Rating */}
        {hotel.reviews && (
          <div className="flex items-center mb-2">
            <div className="flex text-yellow-400">
              {[...Array(5)].map((_, i) => (
                <svg 
                  key={i} 
                  className={`w-5 h-5 ${i < Math.floor(hotel.reviews.rating) ? 'text-yellow-400' : 'text-gray-300'}`} 
                  fill="currentColor" 
                  viewBox="0 0 20 20"
                >
                  <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                </svg>
              ))}
            </div>
            <span className="ml-2 text-gray-600">
              {hotel.reviews.rating} ({hotel.reviews.count} reviews)
            </span>
          </div>
        )}

        {/* Location */}
        <div className="flex items-center mb-3">
          <svg className="h-5 w-5 text-gray-500 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
          </svg>
          <span className="text-gray-600">
            {hotel.geocode ? `${hotel.geocode.latitude}, ${hotel.geocode.longitude}` : 'Location not available'}
          </span>
        </div>

        {/* Price */}
        <div className="mt-4 flex justify-between items-center">
          <div>
            <span className="text-gray-600">Price:</span>
            <span className="ml-2 text-xl font-bold">
              {hotel.price1 ? `$${hotel.price1}` : 'Price not available'}
            </span>
            <span className="text-sm text-gray-500">/night</span>
          </div>

          <button className="bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded">
            View Details
          </button>
        </div>
      </div>
    </div>
  );
};

export default HotelCard;
