import React, { useState } from 'react';
import CitySearch from './components/formSearch/CitySearch';
import HotelResults,{fetchHotels}from './components/hotelResult/HotelList';
import DatePicker from 'react-datepicker';
import  'react-datepicker/dist/react-datepicker.module.css';

const HotelSearchApp: React.FC = () => {
  const [hotels, setHotels] = useState<any[]>([]);
  const [selectedCityId, setSelectedCityId] = useState<string | any>(null);

  const handleSearchHotels = async (checkIn: string, checkOut: string, adults: number, children: number) => {
    if (selectedCityId) {
      const hotelData = await fetchHotels(selectedCityId, checkIn, checkOut, adults, children);
      setHotels(hotelData);
    }
  };

  return (
    <div>
      <CitySearch onSelectCity={(cityId) => setSelectedCityId(cityId)} />


      <HotelResults hotels={hotels} />
    </div>
  );
};

export default HotelSearchApp;
