import React, { useState } from 'react';
import CitySearch from './CitySearch';
import {HotelResults} from './HotelResults';
import axios from 'axios'

export const fetchHotels = async (
    cityId: string,
    checkIn: string,
    checkOut: string,
    adults: number,
    children: number
  ) => {
    try {
      const response = await axios.get('https://api.makcorps.com/city', {
        params: {
          api_key: 'YOUR_API_KEY',
          cityid: cityId, 
          pagination: 0,
          cur: 'USD',
          rooms: 1,
          adults: adults,
          children: children,
          checkin: checkIn,
          checkout: checkOut,
        },
      });
      return response.data;
    } catch (error) {
      console.error('Error fetching hotels:', error);
      return [];
    }
  };




const HotelSearchApp: React.FC = () => {
  const [selectedCityId, setSelectedCityId] = useState<string | null>(null);
  const [checkIn, setCheckIn] = useState<string>(''); 
  const [checkOut, setCheckOut] = useState<string>(''); 
  const [adults, setAdults] = useState<number>(1); 
  const [children, setChildren] = useState<number>(0);
  const [roomType, setRoomType] = useState<string>(''); 
  const [hotels, setHotels] = useState([]); 


  const handleCitySelection = (cityId: string) => {
    setSelectedCityId(cityId);
  };

  // دالة جلب الفنادق
  const handleSearchHotels = async () => {
    if (!selectedCityId || !checkIn || !checkOut) {
      alert('من فضلك، أكمل جميع البيانات');
      return;
    }
    try {
      const response = await fetchHotels(selectedCityId, checkIn, checkOut, adults, children);
      setHotels(response);
    } catch (error) {
      console.error('فشل في جلب الفنادق:', error);
    }
  };

  return (
    <div>
      <h1>Hotel Search App</h1>
      <CitySearch onSelectCity={handleCitySelection} />

      {selectedCityId && (
        <div>
          <p>Selected City ID: {selectedCityId}</p>

          {/* Date Pickers */}
          <label>Check-In:</label>
          <input type="date" value={checkIn} onChange={(e) => setCheckIn(e.target.value)} />

          <label>Check-Out:</label>
          <input type="date" value={checkOut} onChange={(e) => setCheckOut(e.target.value)} />

          {/* Number Selectors */}
          <label>Adults:</label>
          <input type="number" min="1" value={adults} onChange={(e) => setAdults(parseInt(e.target.value))} />

          <label>Children:</label>
          <input type="number" min="0" value={children} onChange={(e) => setChildren(parseInt(e.target.value))} />

          {/* Room Type Filter */}
          <label>Room Type:</label>
          <select value={roomType} onChange={(e) => setRoomType(e.target.value)}>
            <option value="">Any</option>
            <option value="single">Single</option>
            <option value="double">Double</option>
            <option value="suite">Suite</option>
          </select>


          <button onClick={handleSearchHotels}>Search Hotels</button>
        </div>
      )}


      {hotels.length > 0 && <HotelResults hotels={hotels} />}
    </div>
  );
};

export default HotelSearchApp;
